import React, { useState } from 'react';
import { NovoColaborador } from '../types';
import { departamentos } from '../data/colaboradores';
import CheckCircleRoundedIcon from '@mui/icons-material/CheckCircleRounded';

interface CadastrarColaboradorProps {
  onBack: () => void;
  onNext: (colaborador: NovoColaborador) => void;
}

const CadastrarColaborador: React.FC<CadastrarColaboradorProps> = ({ onBack, onNext }) => {
  const [formData, setFormData] = useState<NovoColaborador>({
    nome: 'João da Silva',
    email: '',
    departamento: '',
    ativarAoCriar: true
  });

  const [currentStep, setCurrentStep] = useState<'basicas' | 'profissionais'>('basicas');

  const handleInputChange = (field: keyof NovoColaborador, value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleNext = () => {
    if (currentStep === 'basicas') {
      setCurrentStep('profissionais');
    } else {
      onNext(formData);
    }
  };

  const isBasicasValid = formData.nome.trim() !== '';
  const isProfissionaisValid = formData.departamento !== '';

  return (
    <div className="p-8 min-h-screen flex flex-col">
      <div className="mb-6">
        <nav className="flex items-center space-x-2 text-sm text-gray-500 mb-4">
          <span>Colaboradores</span>
          <span>•</span>
          <span>Cadastrar Colaborador</span>
        </nav>
        
        {/* Progress Bar com Percentual */}
        <div className="w-full bg-green-100 rounded-full h-1 mb-6 relative">
          <div 
            className="bg-flugo-green h-1 rounded-full transition-all duration-300"
            style={{ width: currentStep === 'basicas' ? '0%' : '50%' }}
          ></div>
          {/* Percentual no final da barra verde */}
          <div className="absolute right-0 top-2 text-sm font-medium text-gray-700">
            {currentStep === 'basicas' ? '0%' : '50%'}
          </div>
        </div>
      </div>

      {currentStep === 'basicas' && (
        <div className="flex flex-1">
            {/* Sidebar com Steps */}
            <div className="w-60 pr-8 pt-1">
              <div className="space-y-10 relative">
                {/* Linha conectora */}
                <div className="absolute left-3 top-7 w-px h-8 bg-gray-300"></div>
                
                <div className="flex items-center space-x-2">
                  <div className="w-6 h-6 bg-flugo-green rounded-full flex items-center justify-center text-white font-semibold relative z-10">
                    1
                  </div>
                  <span className="text-base font-medium text-gray-900">Infos Básicas</span>
                </div>
                
                <div className="flex items-center space-x-2">
                  <div className="w-6 h-6 bg-gray-300 rounded-full flex items-center justify-center text-gray-500 font-semibold relative z-10">
                    2
                  </div>
                  <span className="text-base font-medium text-gray-400">Infos Profissionais</span>
                </div>
              </div>
          </div>
          
          {/* Conteúdo Principal */}
          <div className="flex-1 max-w-2xl">
            <h2 className="text-2xl font-semibold text-gray-600 mb-8">Informações Básicas</h2>
            
            <div className="space-y-6">
              <div>
                <div className="relative">
                  <input
                    type="text"
                    value={formData.nome}
                    onChange={(e) => handleInputChange('nome', e.target.value)}
                    className="w-full px-3 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-flugo-green focus:border-flugo-green peer"
                    placeholder="Nome completo"
                  />
                  <label className="absolute -top-2 left-3 bg-white px-1 text-sm font-medium text-gray-500 peer-focus:text-flugo-green transition-colors">
                    Título
                  </label>
                </div>
              </div>

            <div>
                <div className="relative">
                  <input
                    type="email"
                    value={formData.email}
                    onChange={(e) => handleInputChange('email', e.target.value)}
                    className="w-full px-3 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-flugo-green focus:border-flugo-green peer"
                    placeholder="e.g. john@gmail.com"
                  />
                  <label className="absolute -top-2 left-3 bg-white px-1 text-sm font-medium text-gray-500 peer-focus:text-flugo-green transition-colors">
                    E-mail
                    E-mail
                  </label>
                </div>
              </div>

              <div className="flex items-center space-x-3">
                <button
                  type="button"
                  onClick={() => handleInputChange('ativarAoCriar', !formData.ativarAoCriar)}
                  className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors focus:outline-none focus:ring-2 focus:ring-flugo-green focus:ring-offset-2 ${
                    formData.ativarAoCriar ? 'bg-flugo-green' : 'bg-gray-300'
                  }`}
                >
                  <span
                    className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                      formData.ativarAoCriar ? 'translate-x-6' : 'translate-x-1'
                    }`}
                  />
                </button>
                <span className="text-sm font-medium text-gray-700">Ativar ao criar</span>
              </div>
            </div>

            <div className="flex justify-between mt-8">
              <button
                onClick={onBack}
                className="px4 py-2 text-gray-700 hover:text-gray-800 font-medium transition-colors"
              >
                Voltar
              </button>
              <button
                onClick={handleNext}
                disabled={!isBasicasValid}
                className="bg-flugo-green hover:bg-flugo-green-dark disabled:bg-gray-300 disabled:cursor-not-allowed text-white px-4 py-3 rounded-lg font-medium transition-colors"
              >
                Próximo
              </button>
            </div>
          </div>
        </div>
      )}

      {currentStep === 'profissionais' && (
        <>
          <div className="flex flex-1">
            {/* Sidebar com Steps */}
            <div className="w-50 pr-8 pt-1">
              <div className="space-y-10 relative">
                {/* Linha conectora */}
                <div className="absolute left-3 top-8 w-px h-6 bg-gray-300"></div>
                
                <div className="flex items-center space-x-2">
                  <div className="w-6 h-6 bg-flugo-green rounded-full flex items-center justify-center text-white font-semibold relative z-10">
                    <CheckCircleRoundedIcon style={{ fontSize: '16px' }} />
                  </div>
                  <span className="text-base font-medium text-gray-900">Infos Básicas</span>
                </div>
                
                <div className="flex items-center space-x-2">
                  <div className="w-6 h-6 bg-flugo-green rounded-full flex items-center justify-center text-white font-semibold relative z-10">
                    2
                  </div>
                  <span className="text-base font-medium text-gray-900">Infos Profissionais</span>
                </div>
              </div>
            </div>

            {/* Conteúdo Principal */}
            <div className="flex-1">
              <h1 className="text-2xl font-semibold text-gray-600 mb-8">Informações Profissionais</h1>
              
              <div className="relative w-full">
                <select
                  value={formData.departamento}
                  onChange={(e) => handleInputChange('departamento', e.target.value)}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-flugo-green focus:border-flugo-green appearance-none bg-white text-gray-600"
                >
                  <option value="">Selecione um departamento</option>
                  {departamentos.map((dept) => (
                    <option key={dept} value={dept}>{dept}</option>
                  ))}
                </select>
                {/* Ícone de dropdown */}
                <svg className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400 pointer-events-none" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </div>
            </div>
          </div>

          {/* Botões no final da tela */}
          <div className="flex justify-between items-center mt-auto pt-8">
            <button
              onClick={() => setCurrentStep('basicas')}
              className="px-6 py-2 text-gray-800 hover:text-gray-900 font-medium transition-colors"
            >
              Voltar
            </button>
            <button
              onClick={handleNext}
              disabled={!isProfissionaisValid}
              className="bg-flugo-green hover:bg-flugo-green-dark disabled:bg-gray-300 disabled:cursor-not-allowed text-white px-5 py-3 rounded-lg font-medium transition-colors"
            >
              Concluir
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default CadastrarColaborador;
