import React, { useState } from 'react';


const departamentos = ['Desenvolvimento', 'Marketing', 'Vendas', 'RH', 'Financeiro'];

interface NovoColaborador {
  nome: string;
  email: string;
  departamento: string;
  ativarAoCriar: boolean;
}

const CadastrarColaborador = () => {
  const [formData, setFormData] = useState<NovoColaborador>({
    nome: 'JoÃ£o da Silva',
    email: '',
    departamento: '',
    ativarAoCriar: true
  });

  const [currentStep, setCurrentStep] = useState<'basicas' | 'profissionais'>('profissionais');

  const handleInputChange = (field: keyof NovoColaborador, value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleNext = () => {
    if (currentStep === 'basicas') {
      setCurrentStep('profissionais');
    } else {
      console.log('Concluir cadastro:', formData);
    }
  };

  const isBasicasValid = formData.nome.trim() !== '';
  const isProfissionaisValid = formData.departamento !== '';

  return (
    <div className="p-8 h-screen flex flex-col bg-white">
      {/* Header */}
      <div className="mb-8">
        <nav className="flex items-center space-x-2 text-sm text-gray-500 mb-6">
          <span>Colaboradores</span>
          <span>â¢</span>
          <span>Cadastrar Colaborador</span>
        </nav>
        
        {/* Progress Bar */}
        <div className="w-full bg-green-100 rounded-full h-2 mb-6 relative">
          <div 
            className="bg-green-500 h-2 rounded-full transition-all duration-300"
            style={{ width: currentStep === 'basicas' ? '0%' : '50%' }}
          ></div>
          <div className="absolute right-0 -top-6 text-sm font-medium text-gray-700">
            {currentStep === 'basicas' ? '0%' : '50%'}
          </div>
        </div>
      </div>

      {/* Main Content - FlexÃ­vel para centralizar */}
      <div className="flex flex-1 min-h-0">
        {currentStep === 'basicas' && (
          <>
            {/* Sidebar */}
            <div className="w-80 pr-8">
              <div className="space-y-6 relative">
                <div className="absolute left-5 top-10 w-px h-8 bg-gray-200"></div>
                
                <div className="flex items-center space-x-4">
                  <div className="w-10 h-10 bg-green-500 rounded-full flex items-center justify-center text-white font-semibold relative z-10">
                    1
                  </div>
                  <span className="text-base font-medium text-gray-900">Infos BÃ¡sicas</span>
                </div>
                
                <div className="flex items-center space-x-4">
                  <div className="w-10 h-10 bg-gray-300 rounded-full flex items-center justify-center text-gray-600 font-semibold relative z-10">
                    2
                  </div>
                  <span className="text-base text-gray-400">Infos Profissionais</span>
                </div>
              </div>
            </div>

            {/* Content */}
            <div className="flex-1 max-w-2xl">
              <h2 className="text-2xl font-semibold text-gray-600 mb-8">InformaÃ§Ãµes BÃ¡sicas</h2>
              
              <div className="space-y-6">
                <div>
                  <label className="block text-sm font-medium text-green-600 mb-2">
                    TÃ­tulo
                  </label>
                  <input
                    type="text"
                    value={formData.nome}
                    onChange={(e) => handleInputChange('nome', e.target.value)}
                    className="w-full px-3 py-2 border-2 border-green-500 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                    placeholder="Nome completo"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    E-mail
                  </label>
                  <input
                    type="email"
                    value={formData.email}
                    onChange={(e) => handleInputChange('email', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500"
                    placeholder="e.g. john@gmail.com"
                  />
                </div>

                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-gray-900">Ativar ao criar</span>
                  <button
                    type="button"
                    onClick={() => handleInputChange('ativarAoCriar', !formData.ativarAoCriar)}
                    className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 ${
                      formData.ativarAoCriar ? 'bg-green-500' : 'bg-gray-300'
                    }`}
                  >
                    <span
                      className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                        formData.ativarAoCriar ? 'translate-x-6' : 'translate-x-1'
                      }`}
                    />
                  </button>
                </div>
              </div>
            </div>
          </>
        )}

        {currentStep === 'profissionais' && (
          <>
            {/* Sidebar */}
            <div className="w-80 pr-8">
              <div className="space-y-6 relative">
                <div className="absolute left-5 top-10 w-px h-8 bg-gray-200"></div>
                
                <div className="flex items-center space-x-4">
                  <div className="w-10 h-10 bg-green-500 rounded-full flex items-center justify-center text-white font-semibold relative z-10">
                    â
                  </div>
                  <span className="text-base font-medium text-gray-900">Infos BÃ¡sicas</span>
                </div>
                
                <div className="flex items-center space-x-4">
                  <div className="w-10 h-10 bg-green-500 rounded-full flex items-center justify-center text-white font-semibold relative z-10">
                    2
                  </div>
                  <span className="text-base font-medium text-gray-900">Infos Profissionais</span>
                </div>
              </div>
            </div>

            {/* Content - Centralizando verticalmente */}
            <div className="flex-1 flex flex-col justify-center max-w-2xl">
              <div className="space-y-8">
                <h1 className="text-2xl font-semibold text-gray-600">InformaÃ§Ãµes Profissionais</h1>
                
                <div className="relative w-full">
                  <select
                    value={formData.departamento}
                    onChange={(e) => handleInputChange('departamento', e.target.value)}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500 appearance-none bg-white text-gray-600"
                  >
                    <option value="">Selecione um departamento</option>
                    {departamentos.map((dept) => (
                      <option key={dept} value={dept}>{dept}</option>
                    ))}
                  </select>
                  <svg className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400 pointer-events-none" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                  </svg>
                </div>
              </div>
            </div>
          </>
        )}
      </div>

      {/* Footer com botÃµes - Posicionado com margem adequada */}
      <div className="flex justify-between items-center pt-8 mt-8">
        <button
          onClick={() => currentStep === 'profissionais' ? setCurrentStep('basicas') : console.log('Voltar')}
          className="px-6 py-2 text-gray-700 hover:text-gray-900 font-medium transition-colors"
        >
          Voltar
        </button>
        
        <button
          onClick={handleNext}
          disabled={currentStep === 'basicas' ? !isBasicasValid : !isProfissionaisValid}
          className="bg-green-500 hover:bg-green-600 disabled:bg-gray-300 disabled:cursor-not-allowed text-white px-8 py-3 rounded-lg font-medium transition-colors"
        >
          {currentStep === 'basicas' ? 'PrÃ³ximo' : 'Concluir'}
        </button>
      </div>

      {/* BotÃµes para testar as telas */}
      <div className="mt-4 flex gap-2 justify-center">
        <button
          onClick={() => setCurrentStep('basicas')}
          className="px-3 py-1 bg-blue-100 text-blue-700 rounded text-sm"
        >
          Ver Tela 1
        </button>
        <button
          onClick={() => setCurrentStep('profissionais')}
          className="px-3 py-1 bg-blue-100 text-blue-700 rounded text-sm"
        >
          Ver Tela 2
        </button>
      </div>
    </div>
  );
};

export default CadastrarColaborador;
