import React, { useState } from 'react';
import { NovoColaborador } from '../types';
import { departamentos } from '../data/colaboradores';

interface CadastrarColaboradorProps {
  onBack: () => void;
  onNext: (colaborador: NovoColaborador) => void;
}

const CadastrarColaborador: React.FC<CadastrarColaboradorProps> = ({ onBack, onNext }) => {
  const [formData, setFormData] = useState<NovoColaborador>({
    nome: 'JoÃ£o da Silva',
    email: '',
    departamento: '',
    ativarAoCriar: true
  });

  const [currentStep, setCurrentStep] = useState<'basicas' | 'profissionais'>('basicas');

  const handleInputChange = (field: keyof NovoColaborador, value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleNext = () => {
    if (currentStep === 'basicas') {
      setCurrentStep('profissionais');
    } else {
      onNext(formData);
    }
  };

  const isBasicasValid = formData.nome.trim() !== '';
  const isProfissionaisValid = formData.departamento !== '';

  return (
    <div className="p-8">
      <div className="mb-6">
        <nav className="flex items-center space-x-2 text-sm text-gray-500 mb-4">
          <span>Colaboradores</span>
          <span>â¢</span>
          <span>Cadastrar Colaborador</span>
        </nav>
        
        {/* Progress Bar com Percentual */}
        <div className="w-full bg-gray-200 rounded-full h-2 mb-6 relative">
          <div 
            className="bg-flugo-green h-2 rounded-full transition-all duration-300"
            style={{ width: currentStep === 'basicas' ? '50%' : '100%' }}
          ></div>
          {/* Percentual no final da barra verde */}
          <div className="absolute right-0 -top-6 text-sm font-medium text-gray-700">
            {currentStep === 'basicas' ? '50%' : '100%'}
          </div>
        </div>
      </div>

      {currentStep === 'basicas' && (
        <div className="flex">
          {/* Sidebar com Steps */}
          <div className="w-80 pr-8">
            <div className="space-y-6 relative">
              {/* Linha conectora */}
              <div className="absolute left-5 top-10 w-px h-8 bg-gray-200"></div>
              
              <div className="flex items-center space-x-4">
                <div className="w-10 h-10 bg-flugo-green rounded-full flex items-center justify-center text-white font-semibold relative z-10">
                  1
                </div>
                <span className="text-base font-medium text-gray-900">Infos BÃ¡sicas</span>
              </div>
              
              <div className="flex items-center space-x-4">
                <div className="w-10 h-10 bg-gray-300 rounded-full flex items-center justify-center text-gray-600 font-semibold relative z-10">
                  2
                </div>
                <span className="text-base text-gray-400">Infos Profissionais</span>
              </div>
            </div>
          </div>

          {/* ConteÃºdo Principal */}
          <div className="flex-1 max-w-2xl">
            <h2 className="text-xl font-semibold text-gray-700 mb-8">InformaÃ§Ãµes BÃ¡sicas</h2>
            
            <div className="bg-white rounded-lg border border-gray-200 p-6">
              <h3 className="text-lg font-medium text-gray-900 mb-6">InformaÃ§Ãµes BÃ¡sicas</h3>
              
              <div className="space-y-6">
                <div>
                  <label className="block text-sm font-medium text-flugo-green mb-2">
                    TÃ­tulo
                  </label>
                  <input
                    type="text"
                    value={formData.nome}
                    onChange={(e) => handleInputChange('nome', e.target.value)}
                    className="w-full px-3 py-2 border-2 border-flugo-green rounded-md focus:outline-none focus:ring-2 focus:ring-flugo-green focus:border-transparent"
                    placeholder="Nome completo"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    E-mail
                  </label>
                  <input
                    type="email"
                    value={formData.email}
                    onChange={(e) => handleInputChange('email', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-flugo-green focus:border-flugo-green"
                    placeholder="e.g. john@gmail.com"
                  />
                </div>

                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-gray-900">Ativar ao criar</span>
                  <button
                    type="button"
                    onClick={() => handleInputChange('ativarAoCriar', !formData.ativarAoCriar)}
                    className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors focus:outline-none focus:ring-2 focus:ring-flugo-green focus:ring-offset-2 ${
                      formData.ativarAoCriar ? 'bg-flugo-green' : 'bg-gray-300'
                    }`}
                  >
                    <span
                      className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                        formData.ativarAoCriar ? 'translate-x-6' : 'translate-x-1'
                      }`}
                    />
                  </button>
                </div>
              </div>
            </div>

            <div className="flex justify-between mt-8">
              <button
                onClick={onBack}
                className="px-4 py-2 text-gray-600 hover:text-gray-800 font-medium transition-colors"
              >
                Voltar
              </button>
              <button
                onClick={handleNext}
                disabled={!isBasicasValid}
                className="bg-flugo-green hover:bg-flugo-green-dark disabled:bg-gray-300 disabled:cursor-not-allowed text-white px-6 py-2 rounded-lg font-medium transition-colors"
              >
                PrÃ³ximo
              </button>
            </div>
          </div>
        </div>
      )}

      {currentStep === 'profissionais' && (
        <div className="flex">
          {/* Sidebar com Steps */}
          <div className="w-80 pr-8">
            <div className="space-y-6 relative">
              {/* Linha conectora */}
              <div className="absolute left-5 top-10 w-px h-8 bg-gray-200"></div>
              
              <div className="flex items-center space-x-4">
                <div className="w-10 h-10 bg-flugo-green rounded-full flex items-center justify-center text-white font-semibold relative z-10">
                  â
                </div>
                <span className="text-base font-medium text-gray-900">Infos BÃ¡sicas</span>
              </div>
              
              <div className="flex items-center space-x-4">
